# Get licenses on tenant
$license = Invoke-RestMethod -Method Get -Uri $RequestURL -Headers $MicrosoftHeaders

# Translate licenses to a friendly name
$Licenses_Available = foreach ($lic in $license.value) {
    $License_Name = $skuids.keys | Where-Object { $skuids["$_"] -eq $lic.skuPartNumber }
    $available = $lic.prepaidUnits.enabled - $lic.consumedUnits
    $ObjectProperties = ([ordered]@{
            License   = $License_Name
            Available = $available
            SkuID     = $lic.skuId
        })
    New-Object psobject -Property $ObjectProperties 
}

$Licenses_Available